mapper.select:
select distinct u.user_id, u.dept_id, u.user_name, u.nick_name, u.email, u.phonenumber,u.sn_num, u.status, u.create_time,u.plc from sys_user u left join sys_dept d on u.dept_id = d.dept_id left join sys_user_role ur on u.user_id = ur.user_id left join sys_role r on r.role_id = ur.role_id where u.del_flag = '0' and r.role_id = #{roleId}
AND u.user_name like concat('%', #{userName}, '%')
AND u.phonenumber like concat('%', #{phonenumber}, '%')
${params.dataScope}

select * from sys_user u


		AND u.id_card = #{idCard}


		AND u.position_card_no = #{locationCardNo}


		AND u.car_card like concat('%', #{carCardNo}, '%')


		AND u.car_id like concat('%', #{plateNo}, '%')

	AND u.del_flag = '0'

mapper.update:
update sys_user
dept_id = #{deptId}, factory_id = #{factoryId}, user_name = #{userName}, nick_name = #{nickName}, email = #{email}, phonenumber = #{phonenumber}, sex = #{sex}, avatar = #{avatar}, password = #{password}, status = #{status}, login_ip = #{loginIp}, login_date = #{loginDate}, update_by = #{updateBy}, remark = #{remark}, birthday = #{birthday}, id_card = #{idCard}, family_address = #{familyAddress}, car_id = #{carId}, car_card = #{carCard}, face = #{face}, emp_no = #{empNo}, position_card_no = #{positionCardNo}, sn_num = #{snNum}, join_date = #{joinDate}, sended = #{sended}, plc = #{plc}, update_time = getdate()
where user_id = #{userId}
